mapper.update:
UPDATE `article`
user_id = #{userId}, title = #{title}, content = #{content}, images = #{images}, classify = #{classify}, mode = #{mode}, read_count = #{readCount},
WHERE id = #{id} AND deleted = 0;

mapper.delete:
DELETE
FROM `r_user_like_article`
WHERE user_id = #{userId}
  AND article_id = #{articleId};

mapper.select:
SELECT a.id,a.deleted,a.creator,a.modifier,a.create_time,
a.modify_time,a.author_id,a.title,a.images,
a.classify,a.mode,a.read_count, ru.article_id > 0 as `isLike`
FROM `article` a
LEFT JOIN `r_user_like_article` ru ON a.id = ru.article_id and ru.user_id = #{query.userId}

    AND a.deleted = 0

        AND a.author_id = #{query.authorId}
        AND a.title = #{query.title}
        AND a.content = #{query.content}
        AND a.images = #{query.images}
        AND a.classify like "%"#{query.classify}"%"
        AND a.mode = #{query.mode}
        AND a.read_count = #{query.readCount}

SELECT id, article_id, user_id
FROM `r_user_like_article`
WHERE user_id = #{userId}
  AND article_id = #{articleId}
LIMIT 1;

SELECT a.id,a.deleted,a.creator,a.modifier,a.create_time,a.modify_time,a.author_id,a.title,a.images,a.classify,a.mode,a.read_count, ru.article_id > 0 as `isLike` FROM `article` a RIGHT JOIN `r_user_like_article` ru ON a.id =
ru.article_id and ru.user_id = #{userId}

    AND a.deleted = 0
